    private void btnloginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnloginActionPerformed
        // TODO add your handling code here:
        try {
            Statement statement =(Statement) Koneksi.getKoneksi().createStatement();
            ResultSet result=statement.executeQuery("select*from user where "+"username='"+txtusername.getText()+"'");
            if (result.next()){
                if (txtpassword.getText().equals(result.getString("password"))){
                    new MenuUtama().show();
                    this.dispose();

                }else{
                    JOptionPane.showMessageDialog(null,"Password salah");
                    txtpassword.setText("");
                    txtusername.requestFocus();
                }
            }else{
                JOptionPane.showMessageDialog(null,"User Tidak DItemukan");
                txtusername.setText("");
                txtpassword.setText("");
                txtusername.requestFocus();
            }
        }catch (Exception e){
            JOptionPane.showMessageDialog(null, "gagal");
        }
    }//GEN-LAST:event_btnloginActionPerformed

